"use client"

import { useState, useEffect } from "react"
import { Link, useLocation, useNavigate } from "react-router-dom"
import { handleSuccess } from "../../utils"
import "./Sidebar.css"

function Sidebar() {
  const [isCollapsed, setIsCollapsed] = useState(false)
  const location = useLocation()
  const navigate = useNavigate()
  const loggedInUser = localStorage.getItem("loggedInUser")

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed)
  }

  const handleLogout = () => {
    localStorage.removeItem("token")
    localStorage.removeItem("loggedInUser")
    handleSuccess("Logged out successfully")
    setTimeout(() => {
      navigate("/login")
    }, 1000)
  }

  const menuItems = [
    { path: "/dashboard", icon: "üè†", label: "Dashboard" },
    { path: "/patient-management", icon: "üë•", label: "Patients" },
    { path: "/doctor-management", icon: "üë®‚Äç‚öïÔ∏è", label: "Doctors" },
    { path: "/appointment-scheduling", icon: "üìÖ", label: "Appointments" },
    { path: "/medical-history", icon: "üìã", label: "Medical History" },
    { path: "/lab-test-management", icon: "üß™", label: "Lab Tests" },
    { path: "/billing-payments", icon: "üí∞", label: "Billing" },
    { path: "/pharmacy-module", icon: "üíä", label: "Pharmacy" },
    { path: "/ward-management", icon: "üõèÔ∏è", label: "Ward Management" },
  ]

  // Add collapsed class to content areas
  useEffect(() => {
    const contentElements = document.querySelectorAll(
      ".dashboard-content, .patient-management-content, .doctor-management-content, .appointment-scheduling-content, .medical-history-content, .lab-test-management-content, .billing-payments-content, .pharmacy-module-content, .ward-management-content",
    )

    contentElements.forEach((element) => {
      if (isCollapsed) {
        element.classList.add("sidebar-collapsed")
      } else {
        element.classList.remove("sidebar-collapsed")
      }
    })
  }, [isCollapsed])

  return (
    <div className={`sidebar ${isCollapsed ? "collapsed" : ""}`}>
      <button className="sidebar-toggle" onClick={toggleSidebar}>
        {isCollapsed ? "‚Üí" : "‚Üê"}
      </button>

      <div className="sidebar-header">
        <h2>üè• HMS</h2>
        <p>Hospital Management</p>
      </div>

      <div className="user-info">
        <div className="user-avatar">{loggedInUser ? loggedInUser.charAt(0).toUpperCase() : "U"}</div>
        <div className="user-details">
          <div className="user-name">{loggedInUser || "User"}</div>
          <div className="user-role">Administrator</div>
        </div>
      </div>

      <nav className="sidebar-nav">
        {menuItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`nav-item ${location.pathname === item.path ? "active" : ""}`}
          >
            <span className="nav-icon">{item.icon}</span>
            <span className="nav-label">{item.label}</span>
            {isCollapsed && <div className="nav-item-tooltip">{item.label}</div>}
          </Link>
        ))}
      </nav>

      <div className="sidebar-footer">
        <button onClick={handleLogout} className="logout-btn">
          <span className="nav-icon">üö™</span>
          <span className="btn-text">Logout</span>
        </button>
      </div>
    </div>
  )
}

export default Sidebar
